import streamlit as st

name = st.text_input('Enter your name')
if name:
    st.title(f"Hello {name}")
else:
    st.title("Hello Brother")
player = st.sidebar.selectbox('choose your fav player', ['Virat Kohli', 'MSD', 'ABD', 'Baaz', 'Rohit'])
st.write(f"you have chosen {player}. Excelent choice")
if player == 'MSD':
    st.success('you are a genius')
    st.balloons()

if st.button('click me'):
    st.balloons()

col1, col2 = st.columns(2)
with col1:
    st.image('https://static.toiimg.com/thumb/msid-108681057,width-400,resizemode-4/108681057.jpg', width=500)
with col2: 
    st.image('data:image/jpeg;base64,', width=500)


import pandas as pd
import sys

file = st.file_uploader('upload file')
if file:
    if file.type != 'text/csv':
        st.error('Please upload a CSV file')
        sys.exit(1)
    
    st.success('file uploaded')
    df = pd.read_csv(file)

    st.subheader('summary stats')

    st.write(df.head())
    st.write(df.describe())

    personalities = df['Personality'].unique()
    selected_personality = st.selectbox('filter by personalities', personalities)
    filtered_df = df[df['Personality'] == selected_personality] # new filtered table
    st.write(filtered_df.head())